<?php

namespace Brasa\ContabilidadBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * CtbMovimientosConceptosRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CtbMovimientosConceptosRepository extends EntityRepository
{
    public function DevMovimientosConceptosOrdenMovimientoTipo() {
        $objRepositorio = $this->getEntityManager()->getRepository('BrasaContabilidadBundle:CtbMovimientosConceptos');    
        $objQuery = $objRepositorio->createQueryBuilder('mc')
            ->orderBy('mc.codigoMovimientoTipoFk', 'ASC')
            ->getQuery();           
        
        return $objQuery->getResult();
    }    
}