<?php

namespace Brasa\GeneralBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * DireccionesRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class GenTerceroDireccionRepository extends EntityRepository
{
    public function DevDireccionesTercero($codigoTerceroFk)
    {
        $em = $this->getEntityManager();
        $arDirecciones = new \Brasa\GeneralBundle\Entity\GenTerceroDireccion();
        $arDirecciones = $em->getRepository('BrasaFrontEndBundle:GenTerceroDireccion')->findBy(array('codigoTerceroFk' => $codigoTerceroFk));
        
        return $arDirecciones;
    }
}