<?php

namespace Brasa\RecursoHumanoBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RhuEmpleadoDotacionRepository extends EntityRepository {
    
    public function listaDQL($strIdentificacion = "", $codigoCentroCosto = "") {        
        $dql   = "SELECT d, e FROM BrasaRecursoHumanoBundle:RhuEmpleadoDotacion d JOIN d.empleadoRel e WHERE d.codigoEmpleadoDotacionPk <> 0";
        if($strIdentificacion != "" ) {
            $dql .= " AND e.numeroIdentificacion LIKE '%" . $strIdentificacion . "%'";
        }
        if($codigoCentroCosto != "" ) {
            $dql .= " AND e.codigoCentroCostoFk = " . $codigoCentroCosto;
        }
        $dql .= " ORDER BY d.fecha";
        return $dql;
    }       
}