<?php

namespace Brasa\RecursoHumanoBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RhuPagoExportarRepository extends EntityRepository {       
     
    //total valor registros de la tabla pagoExportar
    public function totalValorResgistrosPagoExportar() {
        $em = $this->getEntityManager();
        $dql   = "SELECT SUM(pe.vrPago) FROM BrasaRecursoHumanoBundle:RhuPagoExportar pe ";
        $query = $em->createQuery($dql);
        $duoTotal = $query->getSingleScalarResult();
        return $duoTotal;
    }
}