<?php

namespace Brasa\RecursoHumanoBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RhuVacacionDisfruteRepository extends EntityRepository {        
    
    public function listaDql($strCodigoCentroCosto = "", $strIdentificacion = "") {        
        $em = $this->getEntityManager();
        $dql   = "SELECT v, e FROM BrasaRecursoHumanoBundle:RhuVacacionDisfrute v JOIN v.empleadoRel e WHERE v.codigoVacacionDisfrutePk <> 0";
        
        if($strCodigoCentroCosto != "") {
            $dql .= " AND v.codigoCentroCostoFk = " . $strCodigoCentroCosto;
        }   
        if($strIdentificacion != "" ) {
            $dql .= " AND e.numeroIdentificacion LIKE '%" . $strIdentificacion . "%'";
        }
        return $dql;
    }    
    
    public function dias($codigoEmpleado, $fechaDesde, $fechaHasta) {
        $em = $this->getEntityManager();
        $arVacaciones = new \Brasa\RecursoHumanoBundle\Entity\RhuVacacion();
        $dql   = "SELECT v FROM BrasaRecursoHumanoBundle:RhuVacacionDisfrute v "
                . "WHERE v.codigoEmpleadoFk = " . $codigoEmpleado
                . " AND v.fechaDesde <= '" . $fechaHasta->format('Y-m-d') . "' "
                . " AND v.fechaHasta >= '" . $fechaDesde->format('Y-m-d') . "' ";
        $query = $em->createQuery($dql);
        $arVacaciones = $query->getResult();
        $intDiasDevolver = 0;
        foreach ($arVacaciones as $arVacacion) {
            $dateFechaDesde =  "";
            $dateFechaHasta =  "";
            
            if($arVacacion->getFechaDesde() <  $fechaDesde == true) {
                $dateFechaDesde = $fechaDesde;
            } else {
                $dateFechaDesde = $arVacacion->getFechaDesde();
            }

            if($arVacacion->getFechaHasta() >  $fechaHasta == true) {
                $dateFechaHasta = $fechaHasta;
            } else {
                $dateFechaHasta = $arVacacion->getFechaHasta();
            }
            if($dateFechaDesde != "" && $dateFechaHasta != "") {
                $intDias = $dateFechaDesde->diff($dateFechaHasta);
                $intDias = $intDias->format('%a');
                $intDiasDevolver += $intDias + 1;
            }
        }
        return $intDiasDevolver;
    }    
    
    public function diasVacacionesDisfrute($fechaDesde, $fechaHasta, $codigoEmpleado) {
        $em = $this->getEntityManager();
        $strFechaDesde = $fechaDesde->format('Y-m-d');
        $strFechaHasta = $fechaHasta->format('Y-m-d');
        $dql = "SELECT vd FROM BrasaRecursoHumanoBundle:RhuVacacionDisfrute vd "
                . "WHERE (((vd.fechaDesde BETWEEN '$strFechaDesde' AND '$strFechaHasta') OR (vd.fechaHasta BETWEEN '$strFechaDesde' AND '$strFechaHasta')) "
                . "OR (vd.fechaDesde >= '$strFechaDesde' AND vd.fechaDesde <= '$strFechaHasta') "
                . "OR (vd.fechaHasta >= '$strFechaHasta' AND vd.fechaDesde <= '$strFechaDesde')) "
                . "AND vd.codigoEmpleadoFk = '" . $codigoEmpleado . "'";
        $objQuery = $em->createQuery($dql);  
        $arVacacionesDisfrute = $objQuery->getResult();         
        $intDiasVacaciones = 0;
        foreach ($arVacacionesDisfrute as $arVacacionDisfrute) {
            $intDiaInicio = 1;            
            $intDiaFin = 30;
            if($arVacacionDisfrute->getFechaDesde() <  $fechaDesde) {
                $intDiaInicio = 1;                
            } else {
                $intDiaInicio = $arVacacionDisfrute->getFechaDesde()->format('j');
            }
            if($arVacacionDisfrute->getFechaHasta() > $fechaHasta) {
                $intDiaFin = 30;                
            } else {
                $intDiaFin = $arVacacionDisfrute->getFechaHasta()->format('j');
            }            
            $intDiasVacaciones += (($intDiaFin - $intDiaInicio)+1);
        }
        if($intDiasVacaciones > 30) {
            $intDiasVacaciones = 30;
        }
        return $intDiasVacaciones;                     
    }                        
    
}

