<?php

namespace Brasa\TransporteBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * MovimientosRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TteGuiasRepository extends EntityRepository {
    public function GuiasDespachoDetalle($codigoDespacho) {        
        $em = $this->getEntityManager();
        $dql   = "SELECT guias FROM BrasaTransporteBundle:TteGuias guias WHERE guias.codigoDespachoFk = " . $codigoDespacho;
        $query = $em->createQuery($dql);        
        return $query;
    }
    
    public function GuiasFacturaDetalle($codigoFactura) {        
        $em = $this->getEntityManager();
        $dql   = "SELECT guias FROM BrasaTransporteBundle:TteGuias guias WHERE guias.codigoFacturaFk = " . $codigoFactura;
        $query = $em->createQuery($dql);        
        return $query;
    }    
    
    public function GuiasPendientesDespacho() {        
        $em = $this->getEntityManager();
        $dql   = "SELECT guias FROM BrasaTransporteBundle:TteGuias guias";
        $query = $em->createQuery($dql);        
        return $query;
    }    
}