<?php

namespace Brasa\TransporteBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * MovimientosRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TteRecogidasProgramadasRepository extends EntityRepository {                
    
    public function Lista() {        
        $em = $this->getEntityManager();
        $dql   = "SELECT recogidasprogramadas FROM BrasaTransporteBundle:TteRecogidasProgramadas recogidasprogramadas WHERE recogidasprogramadas.codigoRecogidaProgramadaPk <> 0";        
        $query = $em->createQuery($dql);        
        return $query;
    }        
}